    "\1\1\1\0\1\2\1\3\1\4\1\3\1\5\1\6"+
    "\1\7\1\10\1\2\1\1\1\11\1\12\1\13\1\14"+
    "\1\15\1\16\1\17\1\20\4\16\1\21\1\22\1\23"+
    "\2\24\1\23\1\25\1\26\1\27\1\30\4\16\1\26"+
    "\1\16\1\31\2\16\1\0\1\26\1\32\1\16\1\33"+
    "\1\16\1\34";

  private static int [] zzUnpackAction() {
    int [] result = new int[50];
    int offset = 0;
    offset = zzUnpackAction(ZZ_ACTION_PACKED_0, offset, result);
    return result;
  }

  private static int zzUnpackAction(String packed, int offset, int [] result) {
    int i = 0;       /* index in packed string  */
    int j = offset;  /* index in unpacked array */
    int l = packed.length();
    while (i < l) {
      int count = packed.charAt(i++);
      int value = packed.charAt(i++);
      do result[j++] = value; while (--count > 0);
    }
    return j;
  }


  /**
   * Translates a state to a row index in the transition table
   */
  private static final int [] ZZ_ROWMAP = zzUnpackRowMap();

  private static final String ZZ_ROWMAP_PACKED_0 =
    "\0\0\0\43\0\106\0\106\0\151\0\151\0\106\0\106"+
    "\0\106\0\214\0\257\0\322\0\106\0\106\0\106\0\365"+
    "\0\106\0\u0118\0\106\0\106\0\u013b\0\u015e\0\u0181\0\u01a4"+
    "\0\106\0\106\0\106\0\106\0\u01c7\0\257\0\106\0\u01ea"+
    "\0\u020d\0\106\0\u0230\0\u0253\0\u0276\0\u0299\0\u02bc\0\u02df"+
    "\0\u0118\0\u0302\0\u0325\0\u02bc\0\106\0\u0118\0\u0348\0\u0118"+
    "\0\u036b\0\u0118";

  private static int [] zzUnpackRowMap() {
    int [] result = new int[50];
    int offset = 0;
    offset = zzUnpackRowMap(ZZ_ROWMAP_PACKED_0, offset, result);
    return result;
  }

  private static int zzUnpackRowMap(String packed, int offset, int [] result) {
    int i = 0;  /* index in packed string  */
    int j = offset;  /* index in unpacked array */
    int l = packed.length() - 1;
    while (i < l) {
      int high = packed.charAt(i++) << 16;
      result[j++] = high | packed.charAt(i++);
    }
    return j;
  }

